mapper.select:
select id, section_id, goods_id, sort
from goods_store_section
where section_id = #{sectionId}

select id, column_id, goods_id, sort
from goods_store_column
where column_id = #{columnId}

mapper.update:
UPDATE goods_store_column
SET sort = #{sort}
where id = #{id,jdbcType=INTEGER}

UPDATE goods_store_section
SET sort = #{sort}
where id = #{id,jdbcType=INTEGER}

update.foreach:
update goods_store_column
set sort = #{item.sort,jdbcType=INTEGER}
where column_id = #{item.columnId,jdbcType=INTEGER} and goods_id = #{item.goodsId,jdbcType=INTEGER}

update goods_store_section
set sort = #{item.sort,jdbcType=INTEGER}
where section_id = #{item.sectionId,jdbcType=INTEGER} and goods_id = #{item.goodsId,jdbcType=INTEGER}
